import React from "react";
import Box from "@mui/material/Box";
import { Card, Grid } from "@mui/material";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import { useState } from "react";
import Button from "@mui/material/Button";
import ButtonGroup from "@mui/material/ButtonGroup";
import ReactDOM from "react-dom";

function Dishes({ dname, des, ing, imageKey, price, id , restaurantId,rname}) {
  const [dish, setdish] = useState([]);
  let [counter, setcounter] = useState(0);

  const increment = () => {
    setcounter(counter + 1);
  };
  const decrement = () => {
    setcounter(counter - 1);
  };
  const useModal = () => {
    const [isShowing, setIsShowing] = useState(false);

    function toggle() {
      setIsShowing(!isShowing);
    }

    return {
      isShowing,
      toggle,
    };
  };
  const useModal1 = () => {
    const [isShowing1, setIsShowing1] = useState(false);

    function toggle1() {
      setIsShowing1(!isShowing);
    }

    return {
      isShowing,
      toggle1,
    };
  };

  const addToCart = (dishId, quantity,dname,price) => {
    console.log("here",dishId)
    const subtotal= quantity*price
    if (localStorage.getItem('order')) {
      //console.log("in order",restaurantId, JSON.parse(localStorage.getItem('rescartid')).restaurantId)
      if (localStorage.getItem('rescartid')){
      if (JSON.stringify(JSON.parse(localStorage.getItem('rescartid')).restaurantId )=== restaurantId) {
        console.log("in cart",restaurantId, JSON.parse(localStorage.getItem('order')).restaurantId)
        if(localStorage.getItem("cart")) { 
        localStorage.setItem(
          "cart",
          JSON.stringify([
            ...JSON.parse(localStorage.getItem("cart")),
            { dishId: dishId, dname:dname, quantity: quantity, Price: price, subtotal: subtotal,rname:rname  },
          ])
        )
        localStorage.setItem(
          "rescartid",
          JSON.stringify([
            { restaurantId: restaurantId},
          ])
        )} else { 
        localStorage.setItem(
          "cart",
          JSON.stringify([
            { dishId: dishId, dname:dname, quantity: quantity, Price: price, subtotal: subtotal,rname:rname },
          ])
        )
        localStorage.setItem(
          "rescartid",
          JSON.stringify([
            { restaurantId: restaurantId},
          ])
        )}
      }
    //=====new function here
    else{ toggle()
      toggle1();}
    } else 
      {
      //   if((localStorage.getItem('cart'))) {
      //   localStorage.setItem(
      //     "cart",
      //     JSON.stringify([
      //       ...JSON.parse(localStorage.getItem("cart")),
      //       { dishId: dishId, dname:dname, quantity: quantity, Price: price, subtotal: subtotal, rname:rname },
      //     ])
      //   )
      // }
      // else {
        localStorage.setItem(
          "cart",
          JSON.stringify([
            { dishId: dishId, dname:dname, quantity: quantity, Price: price, subtotal: subtotal ,rname:rname},
          ])
        )
        localStorage.setItem(
          "rescartid",
          JSON.stringify([
            { restaurantId: restaurantId},
          ])
        )
      //}
    }}

  };

  const Modal = ({ isShowing, hide }) =>
    isShowing
      ? ReactDOM.createPortal(
          <React.Fragment>
            <div className="modal-overlay" />
            <div
              className="modal-wrapper"
              aria-modal
              aria-hidden
              tabIndex={-1}
              role="dialog"
            >
              <div className="modal">
              <div style={{ display: "flex", alignItems: "center",fontSize:30}}> {rname} </div>
                <div className="modal-header" style={{justifyContent: 'flex-end'}}>

                  <button
                    type="button"
                    className="modal-close-button"
                    data-dismiss="modal"
                    aria-label="Close"
                    onClick={hide}
                  >
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                <Typography component="div" variant="h5">
                  {dish.dname}
                </Typography>
                <Typography
                  variant="subtitle1"
                  color="text.secondary"
                  component="div"
                >
                  {dish.des}
                  <br />
                  Ingredients: {dish.ing}
                </Typography>
                <Box
                  sx={{ display: "flex", alignItems: "center", pl: 1, pb: 1 }}
                >
                  Price ${price}
                </Box>

                {imageKey && (
                  <CardMedia
                    className="media"
                    component="img"
                    sx={{ width: 151 }}
                    image={`http://localhost:8081/images/${imageKey}`}
                    alt="Live from space album cover"
                  />
                )}
                <ButtonGroup size="small" aria-label="small button group">
                  {counter > 1 ? (
                    <Button
                      key="one"
                      onClick={() => {
                        decrement();
                      }}
                    >
                      -
                    </Button>
                  ) : (
                    <Button
                      disabled
                      key="one"
                      onClick={() => {
                        decrement();
                      }}
                    >
                      -
                    </Button>
                  )}
                  ,
                  <Button key="two" diabled>
                    {counter}
                  </Button>
                  ,
                  <Button
                    key="three"
                    onClick={() => {
                      increment();
                    }}
                  >
                    +
                  </Button>
                  ,
                </ButtonGroup>
                <Button
                  style={{ marginLeft: 30 }}
                  onClick={() => 
                    (JSON.stringify(JSON.parse(localStorage.getItem('order')).restaurantId) === restaurantId)?(
                      console.log("fisrt")
                      ,addToCart(id, counter,dname,price)
      )
                    :(

                      console.log("second", JSON.parse(localStorage.getItem('order')).restaurantId, restaurantId),
                      hide
                      //addToCart(id, counter,dname,price)
                    )
                    //: <Modal isShowing={isShowing} hide={toggle} />
                    // addToCart(id, counter,dname,price) 
                  }
                  disabled={counter===0}
                >
                  {" "}
                  Add to cart
                </Button>
              </div>
            </div>
          </React.Fragment>,
          document.body
        )
      : null;
  const { isShowing, toggle } = useModal();
  //====================================================================

  const { isShowing1, toggle1 } = useModal1();

  const Modal1 = ({ isShowing1, hide }) =>
    isShowing1
      ? ReactDOM.createPortal(
          <React.Fragment>
            <div className="modal-overlay" />
            <div
              className="modal-wrapper"
              aria-modal
              aria-hidden
              tabIndex={-1}
              role="dialog"
            >
              <div className="modal">
              <div style={{ display: "flex", alignItems: "center",fontSize:30}}> {rname} </div>
                <div className="modal-header" style={{justifyContent: 'flex-end'}}>

                  <button
                    type="button"
                    className="modal-close-button"
                    data-dismiss="modal"
                    aria-label="Close"
                    onClick={hide}
                  >
                    <span aria-hidden="true">&times;</span>
                  </button>
                </div>
                <Typography component="div" variant="h2">
                  Create new order?
                </Typography>
