import React, { useState, useEffect, useContext } from "react";

import { Link } from "react-router-dom";
import { Grid, TextField } from "@material-ui/core";
import axios from "axios";
import M from "materialize-css";
import { Formik } from "formik";
import { Button } from "react-bootstrap";
import { BrowserRouter as Router, useHistory } from "react-router-dom";
import {
  CountryDropdown,
  RegionDropdown,
} from "react-country-region-selector-material-ui-new";
import "./UpdateProfile.css";
import Showprofile from "./Showprofile";
import Profilepic from "./Profilepic";
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';

const Pastorders = () => {
  const [headbg, setheadbg] = useState("transparent");
  const [shadow, setshadow] = useState("none");
  const [inputdisplay, setinputdisplay] = useState(0);
  const [orders, setOrders] = useState([])

  useEffect(() => {
    axios
      .post("http://localhost:8081/order/getcusorder", JSON.parse(localStorage.getItem('customer')).customerId)
      .then((res) => {
        console.log(res.data)
      })
  }, [])

  window.addEventListener("scroll", () => {
    if (window.scrollY >= 50) {
      setheadbg("#FFFFFF");
      setshadow("rgb(226 226 226) 0px -2px 0px inset");
      setinputdisplay(1);
    } else {
      setheadbg("transparent");
      setshadow("none");
      setinputdisplay(0);
    }
  });

  return(


    <div className="update">
    <div className="header__upper">
      <div
        className="header__upperheader"
        style={{ backgroundColor: headbg, boxShadow: shadow }}
      >
        <div className="header__upperheaderleft">
          <Link to="/chome">
            <img
              src="https://d3i4yxtzktqr9n.cloudfront.net/web-eats-v2/ee037401cb5d31b23cf780808ee4ec1f.svg "
              alt="uber eats"
            />{" "}
          </Link>
        </div>

        {/* <div className="header__upperheadercenter"   >

<input type="text" placeholder="What are you craving? " />
</div> */}

        <div className="header__upperheaderright">
          <p> Sign out </p>
        </div>
      </div>
    </div>
    <Grid
        style={{
          paddingTop: "100px",
          height: "100vh",
        }}
        container
        direction={"row"}
      >
          Past
      </Grid>
  </div>
  )

}

export default Pastorders;
