import React, { useEffect, useState } from "react";
//import React, { useState, useEffect, useContext } from 'react'
import { Link } from "react-router-dom";
import axios from "axios";
import M from "materialize-css";
import { Button } from "react-bootstrap";
import {
  BrowserRouter as Router,
  useParams,
  useHistory,
} from "react-router-dom";
import Restaurant from "../components/Restaurants";
import { Formik } from "formik";
import {
  Menu,
  LocationOn,
  WatchLater,
  ArrowDownward,
} from "@mui/icons-material";
import Showprofile from "./Showprofile";
import Profilepic from "./Profilepic";
import Center from "../Center";
//import './Adddish.css'

const Adddish = () => {
  const history = useHistory();
  //const [dishData, setdishData] = useState([])
  //const [restaurants, setRestaurants] = useState([])
  //const [image, setImage] = useState([])
  const [headbg, setheadbg] = useState("transparent");
  const [shadow, setshadow] = useState("none");
  const [inputdisplay, setinputdisplay] = useState(0);
  const [dname, setdname] = useState();
  const [ingredients, setingredients] = useState();
  const [ddesc, setddesc] = useState();
  const [cuisineId, setcuisineId] = useState(1);
  const [veg, setveg] = useState("Yes");
  const [nonVeg, setnonveg] = useState("Yes");
  const [vegan, setvegan] = useState("Yes");
  const [categoryId, setcategoryId] = useState(1);
  const [Price, setPrice] = useState(0);
  const [restaurantId,setrestaurantId]= useState();


  window.addEventListener("scroll", () => {
    if (window.scrollY >= 50) {
      setheadbg("#FFFFFF");
      setshadow("rgb(226 226 226) 0px -2px 0px inset");
      setinputdisplay(1);
    } else {
      setheadbg("transparent");
      setshadow("none");
      setinputdisplay(0);
    }
  });
 useEffect(()=>{
   setrestaurantId(JSON.parse(localStorage.getItem("restaurant")).restaurantId)
 },[]);


  async function dishAdd(event) {
    event.preventDefault();


  const dishes = {
    dname,
    ingredients,
    ddesc,
    cuisineId,
    veg,
    nonVeg,
    vegan,
    categoryId,
    Price,
    restaurantId
  };

    axios.post("http://localhost:8081/restaurant/adddish", dishes)
    .then((response) => {
      console.log("update", response);
      if (response.data.error) {
        M.toast({
          html: response.data.error,
          classes: "#c62828 red darken-3",
        });
      } else {
        console.log("update1", response);
        M.toast({
          html: "Updated dish details successfully",
          classes: "#43a047 green darken-1",
        });
      }
    })
    .catch((err) => {
      console.log(err);
    });
    history.push("/rhome");
}

  useEffect(() => {
    // axios.post(`http://localhost:8081/restaurant/getd/${dishId}`,{})
    // .then(response => {
    //     console.log("res",response);
    //     if (response.data.error) {
    //         console.log("res",response);
    //         M.toast({ html: response.data.error, classes: "#c62828 red darken-3" })
    //     }
    //     else {
    //             setdishData(response.data[0])
    //             console.log(response.data[0])
    //             //console.log("resss ",customerData);
    //             localStorage.setItem('dish', JSON.stringify(response.data));
    //     }
    // })
  }, []);

  return (
    <div className="reshome">
      <div className="header__upper">
        <div
          className="header__upperheader"
          style={{ backgroundColor: headbg, boxShadow: shadow }}
        >
          <div className="header__upperheaderleft">
            <Menu />
            <a href="/rhome">
              <img
                src="https://d3i4yxtzktqr9n.cloudfront.net/web-eats-v2/ee037401cb5d31b23cf780808ee4ec1f.svg "
                alt="uber eats"
              />
            </a>
          </div>
          {/* <div className="header__upperheadercenter"   >
           <LocationOn />
           <input type="text" placeholder="What are you craving? " />
        </div> */}

          <div className="header__upperheaderright">
            <p> Sign out </p>
          </div>
        </div>
      </div>

      {/* <div className= "details_img" style={{marginTop:100, marginLeft: 20}}>
                <Showprofile style={{marginTop:150, marginLeft: 20}} />
                <Profilepic style={{marginTop:150, marginLeft: 20}}/>
                </div> */}

      <div className="row" style={{ marginTop: 120, alignContent: Center }}>
        {

              <form
                id="contactForm"
                style={{ width: "40%" }}
                name="sentMessage"

              >
                <h4
                  className="font-weight-normal"
                  style={{
                    fontFamily: "UberMoveText-Medium,Helvetica,sans-serif",
                  }}
                >
                  {" "}
                  Update details
                </h4>
                <br />

                <div className="row">
                  <div className="col-md-12">
                    <div className="form-group">
                      <label style={{ width: "100%" }}>
                        Dish Name
                        <input
                          required
                          type="text"
                          style={{ width: "100%", borderRadius: 0 }}
                          className="form-control"
                          onChange={(e) => setdname(e.target.value)} value={dname}
                        />
                      </label>
                      <p className="help-block text-danger"></p>
                    </div>
                    <div className="form-group">
                      <label style={{ width: "100%" }}>
                        Ingredients{" "}
                        <input
                          type="text"
                          style={{ width: "100%", borderRadius: 0 }}
                          className="form-control"
                          onChange={(e) => setingredients(e.target.value)} value={ingredients}
                        />
                      </label>
                      <p className="help-block text-danger"></p>
                    </div>
                    <div className="form-group">
                      <label style={{ width: "100%" }}>
                        Description{" "}
                        <input
                          type="text"
                          style={{ width: "100%", borderRadius: 0 }}
                          className="form-control"
                          onChange={(e) => setddesc(e.target.value)} value={ddesc}
                        />
                      </label>
                      <p className="help-block text-danger"></p>
                    </div>
                    <div className="form-group">
                      <label style={{ width: "100%" }}>
                        Price{" "}
                        <input
                          type="number"
                          style={{ width: "100%", borderRadius: 0 }}
                          className="form-control"
                          onChange={
                            (e) => {console.log(e.target.value); setPrice(e.target.value);}
                          } value={Price}
                          defaultValue={0}></input>

                      </label>
                      <p className="help-block text-danger"></p>
                    </div>

                    <div className="form-group">
                      <label style={{ width: "100%" }}>
                        Cuisine <input className="form-control"  />
                      </label>
                      <select onChange={(e) => setcuisineId(e.target.value)} value={cuisineId}
                              defaultValue={{ label: "Chinese", value: "1" }}>
                        <option value="1" label="Chinese" />
                        <option value="2" label="Indian" />
                        <option value="3" label="Mediterrian" />
                        <option value="4" label="Lebanese" />
                        <option value="5" label="Italian" />
                        <option value="6" label="Thai" />
                        <option value="7" label="South Indian" />
                        <option value="8" label="Mexican" />
                        <option value="9" label="Greek" />
                        <option value="10" label="Korean" />
                      </select>
                      <p className="help-block text-danger"></p>
                    </div>

                    <div className="form-group">
                      <label style={{ width: "100%" }}>
                        Is Vegitarian
                        <input type="dropdown" className="form-control"  />
                      </label>
                      <select onChange={(e) => setveg(e.target.value)} value={veg}
                              defaultValue={{ label: "Yes", value: "Yes" }} >
                        <option value="Yes" label="Yes" />
                        <option value="No" label="No" />
                      </select>
                      <p className="help-block text-danger"></p>
                    </div>
                    <div className="form-group">
                      <label style={{ width: "100%" }}>
                        Is Non-Vegetarian
                        <input type="dropdown" className="form-control" />
                      </label>
                      <select  onChange={(e) => setnonveg(e.target.value)} value={nonVeg} 
                          defaultValue={{ label: "Yes", value: "Yes" }}>
                        <option value="Yes" label="Yes" />
                        <option value="No" label="No" />
                      </select>
                      <p className="help-block text-danger"></p>
                    </div>
                    <div className="form-group">
                      <label style={{ width: "100%" }}>
                        Is Vegan
                        <input type="dropdown" className="form-control"  />
                      </label>
                      <select onChange={(e) => setvegan(e.target.value)} value={vegan}
                              defaultValue={{ label: "Yes", value: "Yes" }}>
                        <option value="Yes" label="Yes" />
                        <option value="No" label="No" />
                      </select>
                      <p className="help-block text-danger"></p>
                    </div>
                    <div className="form-group">
                      <label style={{ width: "100%" }}>
                        Category{" "}
                        <input type="dropdown" className="form-control"  />
                      </label>
                      <select onChange={(e) => setcategoryId(e.target.value)} value={categoryId} 
                              defaultValue={{ label: "Apetizer", value: 1 }}>
                        <option value="1" label="Apetizer" />
                        <option value="2" label="Salad" />
                        <option value="3" label="Main course" />
                        <option value="4" label="Desert" />
                        <option value="5" label="Beverage" />
                      </select>
                      <p className="help-block text-danger"></p>
                    </div>
                  </div>
                </div>
                <a href="/rhome">
                  <Button
                    type="submit"
                    className="update_button"
                    style={{ width: "100%", borderRadius: 5 }}
                    onClick={dishAdd} 
                  >
                    Save changes
                  </Button>
                </a>
                <br />
                <br />
              </form>
        }
      </div>
    </div>
  );
};

export default Adddish;
