import React, { useState, useEffect, useContext } from "react";

import { Link } from "react-router-dom";
import { Grid, TextField } from "@material-ui/core";
import axios from "axios";
import M from "materialize-css";
import { Formik } from "formik";
import { Button } from "react-bootstrap";
import { BrowserRouter as Router, useHistory } from "react-router-dom";
import {
  CountryDropdown,
  RegionDropdown,
} from "react-country-region-selector-material-ui-new";
import "./UpdateProfile.css";
import Showprofile from "./Showprofile";
import Profilepic from "./Profilepic";

const Checkout = () => {
  const [headbg, setheadbg] = useState("transparent");
  const [shadow, setshadow] = useState("none");
  const [inputdisplay, setinputdisplay] = useState(0);

  window.addEventListener("scroll", () => {
    if (window.scrollY >= 50) {
      setheadbg("#FFFFFF");
      setshadow("rgb(226 226 226) 0px -2px 0px inset");
      setinputdisplay(1);
    } else {
      setheadbg("transparent");
      setshadow("none");
      setinputdisplay(0);
    }
  });

  const submitOrder = () => {
    const order = JSON.parse(localStorage.getItem("order"));
    axios
      .post("http://localhost:8081/order/addorder", {
        customerId: order.customerId,
        restaurantId: order.restaurantId,
        invoiceId: order.invoiceId,
        total: order.total
      })
      .then((response) => {
        console.log("res", response);
        if (response.data.error) {
          console.log("res", response);
          M.toast({
            html: response.data.error,
            classes: "#c62828 red darken-3",
          });
        } else {
          //setcustomerData(response.data[0])
          console.log(response.data[0]);
        }
        const dishesToPass = []
        order.dishes.map((dish) => {
            dishesToPass.push({
                invoiceId : order.invoiceId,
                ...dish
            })
        })
        axios
      .post("http://localhost:8081/order/adddetails", dishesToPass)

      });
  };

  const addAddress = () => {

  }

  useEffect(() => {
    // const customerId =  JSON.parse(localStorage.getItem("customer")).customerId;
    // axios.get(`http://localhost:8081/customerprofile/${customerId}`,{})
    // .then(response => {
    //     console.log("res",response);
    //     if (response.data.error) {
    //         console.log("res",response);
    //         M.toast({ html: response.data.error, classes: "#c62828 red darken-3" })
    //     }
    //     else {
    //             //setcustomerData(response.data[0])
    //             console.log(response.data[0])
    //     }
    // })
  });

  return (
    <section className="section" id="about">
      <div className="update">
        <div className="header__upper">
          <div
            className="header__upperheader"
            style={{ backgroundColor: headbg, boxShadow: shadow }}
          >
            <div className="header__upperheaderleft">
              <Link to="/chome">
                <img
                  src="https://d3i4yxtzktqr9n.cloudfront.net/web-eats-v2/ee037401cb5d31b23cf780808ee4ec1f.svg "
                  alt="uber eats"
                />{" "}
              </Link>
            </div>

            {/* <div className="header__upperheadercenter"   >

    <input type="text" placeholder="What are you craving? " />
 </div> */}

            <div className="header__upperheaderright">
              <p> Sign out </p>
            </div>
          </div>
        </div>
      </div>
      <Grid
        style={{
          paddingTop: "100px",
          height: "100vh",
        }}
        container
        direction={"row"}
      >
        <Grid
          container
          spacing={3}
          xs={6}
          style={{
            border: "1px solid grey",
            height: "100%",
          }}
        >
          {localStorage.getItem("cart") &&
            JSON.parse(localStorage.getItem("cart")).map((dish) => (
              <Grid container item>
                <Grid container xs={4}>
                  {dish.dname}
                </Grid>
                <Grid container xs={4}>
                  {dish.quantity}
                </Grid>
                <Grid container xs={4}>
                  {dish.subtotal}
                </Grid>
              </Grid>
            ))}
          <Grid container item>
            <Grid container xs={4}></Grid>
            <Grid container xs={4}>
              Total Price
            </Grid>
            <Grid container xs={4}>
              {JSON.parse(localStorage.getItem("order")).total}
            </Grid>
          </Grid>
        </Grid>
        <Grid
          container
          spacing={3}
          xs={6}
          style={{
            border: "1px solid grey",
            height: "100%",
          }}
        >
          <Grid container item direction="column" style={{
              padding: "20px"
          }}> 
            Address:

            <TextField label="Address Line 1" />
            <TextField label="Address Line 2" />
            <TextField label="City" />
            <TextField label="State" />
            <TextField label="Zip Code" />
            <br />
            <button onClick={addAddress} style={{
                width: "fill-content",
            }}>Add</button>
          </Grid>

          <Grid
            container
            item
            style={{
              height: "fit-content",
            }}
          >
            <button onClick={submitOrder}>Submit</button>
          </Grid>
        </Grid>
      </Grid>
    </section>
  );
};
export default Checkout;
